import React from 'react';

import convert from 'convert-units';

//import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Fab from '@material-ui/core/Fab';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
//import Selector from './components/Selectors';
import InputField from './components/InputField';

class App extends React.Component {
    constructor() {
        super();
        this.state = {
            distance: 0,
            gas: 0
        }
    }

    handleInputFields = (event) => {
        event.preventDefault();
        console.log(event.target);
        if(event.target.name === 'firstInput') {
            this.setState({
                    firstInput: event.target.value,
                },
                this.handleConversion())
        }
        else if(event.target.name === 'secondInput') {

        }
    }

    handleConversion = () => {
        this.setState({
            secondInput: convert(this.state.firstInput).from(this.state.firstQuantity).to(this.state.secondQuantity)
        })
    }

    render() {
        //console.log(convert().possibilities())
        return (
                <Grid container justify='center' align='center' spacing={3}>
                    <InputField data={{  
                                        input: this.state.firstQuantity, 
                                        label: "Distance",
                                        type: "number",
                                        name: 'miles' }} 
                                handleInput={this.handleInputFields} />

                    <InputField data={{  
                                        input: this.state.secondQuantity, 
                                        label: "Gasolene",
                                        type: "number",
                                        name: 'gallons' }} 
                                handleInput={this.handleInputFields} />

                    <Grid item xs={10}>
                        <Fab color="primary" aria-label="play" >
                            <PlayArrowIcon />
                        </Fab>
                    </Grid>
                </Grid>
        )
    }
}

export default App;
